n Src=\"apt\";release=\"ubuntu\";","\telif grep -Eqi \"centos|red hat|redhat\" /proc/version; then Src=\"yum\";release=\"centos\";","\tfi","}","# get arguments","while [ \"$#\" -gt 0 ]; do","  case \"$1\" in","    -add) ADD=\"$2\"; shift 2;;","    -reset) RESET=\"$2\"; shift 2;;","    -remove) REMOVE=\"$2\"; shift 2;;","    -install) INSTALL=\"$2\"; shift 2;;","\t-noicmp) NOICMP=\"$2\"; shift 2;;","    -geoip) GEOIP=\"$2\"; shift 2;;","    -limit) LIMIT=\"$2\"; shift 2;;","    -io) IO=\"$2\"; shift 2;;","    *) shift 1;;","  esac","done","","success() {","\tGreen=\"\\033[1;32m\"","\tFont=\"\\033[0m\"","\tclear","\tiptables -vnL","\techo -e \"${Green}[+]${Font} $*\"","\texit 0","}","iptables_restart(){","\tservice iptables restart && service ip6tables restart","\tsystemctl restart netfilter-persistent.service","\tsleep 1","}","iptables_reset_rules(){","\tiptables -F && iptables -X && iptables -Z && ip6tables -F && ip6tables -X && ip6tables -Z ","\tiptables -A INPUT -m state --state ESTABLISHED,RELATED -j ACCEPT","\tiptables -A OUTPUT -m state --state ESTABLISHED,RELATED -j ACCEPT","\tip6tables -A INPUT -m state --state ESTABLISHED,RELATED -j ACCEPT","\tip6tables -A OUTPUT -m state --state ESTABLISHED,RELATED -j ACCEPT","}","iptables_save_restart(){","\tiptables-save > /etc/iptables/rules.v4 && ip6tables-save > /etc/iptables/rules.v6","\tiptables_restart","}","uninstall_ipban(){","\tcrontab -l | grep -v \"ipban-update.sh\" | crontab -","\tsystemctl stop netfilter-persistent.service && systemctl disable netfilter-persistent.service","\tiptables_reset_rules","\trm /etc/iptables/rules.v4 && rm /etc/iptables/rules.v6","\tiptables-save","\trm -rf /usr/share/ipban/","\tsuccess \"Uninstalled IPBAN!\"","}","","download_build_dbip (){","\tmkdir -p /usr/share/ipban/ && chmod a+rwx /usr/share/ipban/","cat > \"/usr/share/ipban/download-build-dbip.sh\" << EOF","#!/bin/bash","\tdtmp=\"/usr/share/xt_geoip/tmp/\"","\trm -rf \"\\${dtmp}\" && mkdir -p \"\\${dtmp}\" && cd \"\\${dtmp}\"","\t# Download dbipLite Full","\ttimestamp=\\$(date \"+%Y-%m\")","\tcurl -m 9 -fLO \"https://download.db-ip.com/free/dbip-country-lite-\\${timestamp}.csv.gz\" &> /dev/null","\tcurl -m 9 -fLO \"https://mailfud.org/geoip-legacy/GeoIP-legacy.csv.gz\" -O &> /dev/null","\tif [[ \"\\$?\" != 0 ]]; then","\tcurl -m 9 -fLO \"https://legacy-geoip-csv.ufficyo.com/Legacy-MaxMind-GeoIP-database.tar.gz\" &> /dev/null","\tfi\t\t","\t# Combine all csv and remove duplicates ","\tgzip -dfq *.gz","\tfind . -name \"*.tar\" -exec tar xvf {} \\;","\tfind . -name \"*.tar.gz\" -exec tar xvzf {} \\;","\tcat \"\\${dtmp}GeoIP-legacy.csv\" | tr -d '\"' | cut -d, -f1,2,5 > \"\\${dtmp}GeoIP-legacy-processed.csv\"","\trm \"\\${dtmp}GeoIP-legacy.csv\"","\tcat *.csv > geoip.csv ","\tsort -u geoip.csv -o \"/usr/share/xt_geoip/dbip-country-lite.csv\"","\trm -rf \"\\${dtmp}\"","EOF","\tchmod +x \"/usr/share/ipban/download-build-dbip.sh\"","}","","create_update_sh(){","mkdir -p /usr/share/ipban/ && chmod a+rwx /usr/share/ipban/","cat > \"/usr/share/ipban/ipban-update.sh\" << EOF","#!/bin/bash","\t/usr/share/ipban/download-build-dbip.sh","\tcd /usr/share/xt_geoip/","\t/usr/libexec/xtables-addons/xt_geoip_build -s","\t/usr/lib/xtables-addons/xt_geoip_build -D /usr/share/xt_geoip/","\tcd && rm /usr/share/xt_geoip/dbip-country-lite.csv","\tservice iptables restart && service ip6tables restart","\tsystemctl restart netfilter-persistent.service","\tclear && echo \"Updated IPBAN!\" ","EOF","chmod +x \"/usr/share/ipban/ipban-update.sh\"","}","","iptables_rules(){","\tif [[ ${NOICMP} == *\"y\"* ]]; then","\t\tiptables -A INPUT -p icmp -j DROP","\t\tip6tables -A INPUT -p icmp -j DROP","\tfi\t","\t","\tif [[ ${IO} == *\"I\"* ]]; then","\t\tiptables -A INPUT -m geoip --src-cc \"${GEOIP}\" -j \"${LIMIT}\"","\t\tip6tables -A INPUT -m geoip --src-cc \"${GEOIP}\" -j \"${LIMIT}\"","\tfi","\t","\tif [[ ${IO} == *\"O\"* ]]; then","\t\tiptables  -A OUTPUT -m geoip --dst-cc \"${GEOIP}\" -j \"${LIMIT}\"","\t\tip6tables -A O